import { DatePipe } from '@angular/common';
import { Component, OnInit } from '@angular/core';
import { Cotacao } from './cotacao';
import { CotacaoDolarService } from './cotacaodolar.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css'],
})
export class AppComponent implements OnInit {
  cotacaoAtual = 0;
  cotacaoPorPeriodoLista: Cotacao[] = [];
  showErrorAlert = false;

  constructor(
    private cotacaoDolarService: CotacaoDolarService,
    private dateFormat: DatePipe
  ) {}

  ngOnInit() {
    this.cotacaoDolarService.getCotacaoAtual().subscribe(cotacao => {
      this.cotacaoAtual = cotacao;
    });
  }

  public getCotacaoPorPeriodo(
    dataInicialString: string,
    dataFinalString: string
  ): void {
    this.cotacaoPorPeriodoLista = [];

    const dataInicial = this.dateFormat.transform(dataInicialString, 'MM-dd-yyyy') || '';
    const dataFinal = this.dateFormat.transform(dataFinalString, 'MM-dd-yyyy') || '';

    if (dataInicial && dataFinal && this.compararDatas(dataInicial, dataFinal) && this.validarDataAtual(dataInicial, dataFinal)) {
      this.showErrorAlert = false;
      this.cotacaoDolarService.getCotacaoPorPeriodoFront(dataInicial, dataFinal).subscribe(cotacoes => {
        this.cotacaoPorPeriodoLista = cotacoes;
      });


    } else {
      this.showErrorAlert = true;
    }
  }

  private compararDatas(dataInicial: string, dataFinal: string): boolean {
    const dateInicial = new Date(dataInicial);
    const dateFinal = new Date(dataFinal);

    return dateInicial <= dateFinal;
  }

  private validarDataAtual(dataInicial: string, dataFinal: string): boolean {
    const dataAtual = new Date();
    const dateInicial = new Date(dataInicial);
    const dateFinal = new Date(dataFinal);

    return dateInicial <= dataAtual && dateFinal <= dataAtual;
  }


}
<div class="header mt-4">
  <h1>Cotação do Dólar - {{cotacaoAtual}}</h1>
</div>

<div class="buscarPorPeriodo">
  Histórico de cotações
  <label class="mr-3" for="dataInicial">Data inicial:</label>
  <input id="dataInicial" type="date" name="dataInicial" #dataInicial required>
  <label class="mr-3" for="dataFinal">Data final:</label>
  <input id="dataFinal" type="date" name="dataFinal" #dataFinal required>
  <button mat-raised-button color="primary" type="submit" class="btnPesquisar" (click)="getCotacaoPorPeriodo(dataInicial.value, dataFinal.value)" > Pesquisar </button>
</div>

<div *ngIf="showErrorAlert">
  <p class="erro">Por favor, forneça datas iniciais e finais válidas.</p>
</div>

<div class="mt-3">
  <ul class="ListaCotacoes">
    <li class="listaHistoricoCotacoesHeader">
      <span class="historicoDeCotacoesItemInfo">Data e Hora</span>
      <span class="historicoDeCotacoesItemInfo">Preço</span>
      <span class="historicoDeCotacoesItemInfo">Diferença</span>
    </li>
    <li *ngFor="let item of cotacaoPorPeriodoLista" class="listaHistoricoCotacoesItem">
      <span class="historicoDeCotacoesItemInfo">{{item.data}}</span>
      <span class="historicoDeCotacoesItemInfo">{{item.preco}}</span>
      <span class="historicoDeCotacoesItemInfo">{{item.preco - cotacaoAtual}}</span>
    </li>
  </ul>
</div>
